import React, { Component } from 'react';

class DailyGoalHeading extends Component {
    state = {
        renderAmount: 0
    }
    heading = React.createRef();
    componentDidMount() {
      let len = this.heading.current.offsetWidth
      this.updateState(len);
    }
    updateState = (len)=> {
        // size of header is 200px checkmark is 80 px
        len = Math.floor((len - 200)/80)
        len > 8 ? len = 8 : len = len
        let state = this.state
        state.renderAmount = len
        this.setState({
            state: state
        })
    }
    test = (i) =>{
        return(
            <li>{this.getWeekDay(i)}<br/>{this.getMonthDay(i)}</li>
        )
    }
    negativeDateRenders = (changer) => {
        let len = (this.state.renderAmount/2) -2
        let news =[

        ];
        for(let i = 0; i <= len; i++){
            news.push(this.test(i))
        }
        return(
            [news]
        )
    }
    getWeekDay = (offset) => {
        let weekdays = new Array(
            "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"
        );
        let today = new Date;
        return weekdays[new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset).getDay()]
    }
    getMonthDay = (offset) => {
        let today = new Date;
        today = new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset);
        return today.getDate()
    }
    render() {
        return (
            <div className="dailyheading" ref={this.heading}>
                    <div className="dailyheadingheading">
                        <h1>Daily Goals</h1>
                    </div>
                    <ul>
                        {/* {this.dateRenders()} */}

                        <li>{this.getWeekDay(-4)}<br/>{this.getMonthDay(-4)}</li>
                        <li>{this.getWeekDay(-3)}<br/>{this.getMonthDay(-3)}</li>
                        <li>{this.getWeekDay(-2)}<br/>{this.getMonthDay(-2)}</li>
                        <li>{this.getWeekDay(-1)}<br/>{this.getMonthDay(-1)}</li>
                        <li>{this.getWeekDay(0)}<br/>{this.getMonthDay(0)}</li>
                        {this.negativeDateRenders()}
                        <li>Del<br/>â–¼</li>
                    </ul>
                </div>
        )
    }
}
export default DailyGoalHeading
